cy.wrap(hrefLinks).then(links => {
  const batchSize = 250;
  // Process links in batches (handling async requests in node context)
  Cypress._.chunk(links, batchSize).forEach((batchLinks, batchIndex) => {
    cy.task('batchRequest', {
      baseUrl: url,
      paths: batchLinks
    }, {
      timeout: 120000
    }).then(responses => {
      responses.forEach(({
        href,
        status
      }, index) => {
        const linkIndex = batchIndex * batchSize + index + 1;
        cy.log(`Visited href ${linkIndex}: ${href}`);
        // Validate HTTP status
        if (status) {
          expect(status).to.not.be.oneOf([400, 401, 403, 404, 500, 502, 503]);
          expect(status).to.be.oneOf([200, 301, 302]);
        } else {
          cy.log(`Empty HTTP response for ${href}, skipping...`);
        }
        cy.validateElement('[data-cy="home-page-main-menu"]');
      });
    });
  });
});

