cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    // Define search queries array
    let searchQueries = ['bleu', 'LBO', 'batman', 'nike', 'ike', 'N-ke', 'WhatTHEheck323232', 'ttttt----'];
    // Iterate over each element of the priceRange && productListing arrays
    searchQueries.forEach(query => {
      cy.visit(store, {
        failOnStatusCode: false
      }, {
        responseTimeout: 10000
      }, {
        followRedirect: true
      });
      const textToType = `${query}`;
      // Make sure algolia.net is called
      cy.validateElement('[role="combobox"] > input').detachedDomElement('[role="combobox"] > input').clear({
        force: true
      }).detachedDomElement('[role="combobox"] > input').click({
        force: true
      }).then($input => {
        Cypress._.each(textToType.split(''), char => {
          cy.wrap($input).type(char, {
            delay: 200
          });
        });
      }).waitForFullyLoad(store);
      // Check if there are any captured interceptions
      let capturedInterceptions = [];
      cy.wait(2000).then(() => {
        if (Array.isArray(capturedInterceptions) && capturedInterceptions.length > 0) {
          capturedInterceptions.forEach(interception => {
            expect(interception.response.statusCode).to.be.oneOf([200, 302]);
          });
        } else {
          cy.log("No interceptions were captured within the specified timeout.");
        }
      });
      // Make an HTTP request to the search URL && check the response status code
      cy.request('GET', `/search/${query}`).then(response => {
        expect(response.status).to.not.be.oneOf([400, 404, 500, 503]);
      }).wait(1000);
    });
  });
});
