// The LBO logo and its corresponding href attributes
validateElementAndChildren('[class="nuxt-link-exact-active nuxt-link-active"]');
cy.get('.nuxt-link-exact-active.nuxt-link-active').invoke('attr', 'href').then(hrefValue => {
  console.log('The href value is:', hrefValue);
  expect(hrefValue).to.equal('/');
  cy.request(hrefValue).its('status').should('eq', 200);
});
// Search bar
validateElementAndChildren('[class="autosuggest c-btn-with-input"]');
// Help
const storesToCheck = Cypress.env('urls');
cy.validateElement('[class="c-header-tools__link"]');
cy.url().then(url => {
  cy.fixture('shippingInfo.json').then(shippingInfo => {
    const storesToCheck = [shippingInfo.FRANCE.storeUrl, shippingInfo.SPAIN.storeUrl, shippingInfo.ITALY.storeUrl];
    const hrefAssertions = {
      [storesToCheck[0]]: {
        expected: '/t/faq',
        lang: 'FR'
      },
      [storesToCheck[1]]: {
        expected: '/t/ayuda',
        lang: 'ES'
      },
      [storesToCheck[2]]: {
        expected: '/t/assistenza',
        lang: 'IT'
      }
    };
    // Mapping through shippingInfo.json
    const matchingStore = Object.keys(hrefAssertions).find(store => url.includes(store));
    if (matchingStore) {
      const {
        expected,
        lang
      } = hrefAssertions[matchingStore];
      if (expected) {
        cy.get('[class="c-header-tools__link"]').invoke('attr', 'href').then(hrefValue => {
          expect(hrefValue).to.equal(expected);
          const lastSlashIndex = url.lastIndexOf('/');
          if (lastSlashIndex !== -1 && lastSlashIndex === url.length - 1) {
            url = url.substring(0, lastSlashIndex);
            cy.request(url + hrefValue).its('status').should('eq', 200);
          }
        });
      } else {
        // TO KEEP : logic for Italy or other new stores
      }
    } else {
      throw new Error('Something went wrong with App Store AND/OR Google Play store links, please check your logs...');
    }
  });
});
// Header tools list: Login icon, Wishlist, Shopping Cart, and switch between stores
validateElementAndChildren("[data-cy=header-tool--account]");
// Login
cy.visit(url, {
  failOnStatusCode: false
}, {
  responseTimeout: 10000
}, {
  followRedirect: true
});
// Wishlist
validateElementAndChildren('.c-header-tools__item--favorite');
cy.get('.c-header-tools__list').find('a').each($link => {
  const href = $link.attr('href');
  expect(href).to.exist;
  expect(href).to.not.be.empty;
  retryAnchorGetRequest(url, href);
});
// Switch between stores
cy.get('[class="c-international-switch__list"]').then($currentItem => {
  validateElementAndChildren('[data-cy="lang-link"]');
  cy.get('[data-cy="lang-link"]').click();
  cy.get('.c-header-tools__item--lang').click();
});
// Obtain href/anchor links for stores  
cy.get('.c-dropdown-menu__panel', {
  force: true
}).find('a[href$=".fr"], a[href$=".es"], a[href$=".it"]').each(($link, index, $list) => {
  const href = $link.attr('href');
  cy.request(href).then(response => {
    expect(response.status).to.eq(200);
    expect(response.headers['content-type']).to.include('text/html');
  });
});

// First CMS block
validateElementAndChildren('.c-reinsurance');
// Newsletter block should be available and actionable
cy.acceptCookies();
validateElementAndChildren('.c-newsletter-form');
validateElementAndChildren('.c-btn-with-input.c-btn-with-input--reverse');
cy.get('.c-btn-with-input.c-btn-with-input--reverse').should('exist').as('parentElement');
cy.get('@parentElement').find('> input').should('be.visible').click({
  force: true
}).and('have.focus').then(() => {
  cy.get('@parentElement').type('dummy', {
    delay: 100
  }, {
    force: true
  });
  cy.get('@parentElement').type('newsletter@lbo.com', {
    delay: 200
  }, {
    force: true
  });
});
cy.get('.c-btn.c-btn--reverse').should('not.have.class', 'c-btn--disabled');
// Social medias icons && links
validateElementAndChildren('.c-footer-social-network');
const storesToCheck = Cypress.env('urls');
let oneLinkFrance = Cypress.env('oneLinkFr');
let oneLinkRyses = Cypress.env('oneLinkRyses');
validateElementAndChildren('.c-footer-app');
cy.url().then(url => {
  cy.fixture('shippingInfo.json').then(shippingInfo => {
    cy.get('.c-footer-app__list').should('have.length', 1).within(() => {
      // FR
      if (url.includes(shippingInfo.FRANCE.storeUrl)) {
        cy.get(`a[href*="${oneLinkFrance}"]`).should('have.length', 2).each($el => {
          cy.wrap($el).should('be.visible').click({
            force: true
          }).should('not.have.attr', 'disabled');
        });
        // Ryses
      } else if (url.includes(shippingInfo.SPAIN.storeUrl || shippingInfo.ITALY.storeUrl)) {
        cy.get(`a[href*="${oneLinkRyses}"]`).should('have.length', 2).each($el => {
          cy.wrap($el).should('be.visible').click({
            force: true
          }).should('not.have.attr', 'disabled');
        });
      } else {
        throw new Error('Something went wrong with App Store AND/OR Google Play store links, please check your logs...');
      }
    });
  });
});
// Footer sitemap / CMS
validateElementAndChildren('.c-footer-sitemap');
cy.get('.c-footer-sitemap a').should('have.length.gt', 0).each(($el, index) => {
  const href = $el.attr('href');
  console.log('Retrieved href/anchors links are: ' + href);
  if (href) {
    retryAnchorGetRequest(url, href);
    if (index < cy.$$('.c-footer-sitemap a').length - 1) {
      cy.wait(2000);
    }
  }
});

