  cy.validateElement('.c-order-return__product > .c-checkbox > label');
  cy.get('.c-order-return__product > .c-checkbox > label').eq(0).then(ProductToReturn => {
    if (ProductToReturn.length > 0) {
      cy.get('.c-order-return__product > .c-checkbox > label').eq(0).click({
        force: true
      });
    } else {
      throw new Error("No checkboxes found, something went wrong with this refund request, check your logs.");
    }
