  cy.fixture('shippingInfo.json').then(shippingInfo => {
    cy.get('select[data-v-0326f989]').then($els => {
      expect($els).to.have.length.of.at.least(1);
      if ($els.eq(1).length) {
        cy.wrap($els.eq(1)).select('2').should('have.value', '2');
      } else if ($els.eq(0).length) {
        cy.wrap($els.eq(0)).select('2').should('have.value', '2');
      }
    });
  });
  cy.validateElement('[id^="message-"]');
  cy.get('[id^="message-"]').clear().type('This test is automated');
  cy.validateElement('.c-order-return__notice');
  // Submit the request
  cy.wait(4000); /* TO KEEP FOR CI TESTS */
  cy.validateElement('form > .c-btn');
  cy.get('form > .c-btn').click({
    force: true
  });
  cy.validateElement('.c-order-return__modal');
  cy.validateElement('.c-btn--link');
  cy.get('.c-btn--link').click({
    force: true
  });
  // Make sure the right URL's end-point has been called
