cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    // Checkout process
    cy.visit(store, {
      failOnStatusCode: false
    }, {
      responseTimeout: 10000
    });
    cy.acceptCookies();
    cy.customLogin();
    cy.addShippingAddress();
    cy.acceptCookies();
    // Repeat basket creation process multiple times
    Cypress._.times(Cypress.env('numberOfRuns'), () => cy.basketCreationProcess({
      store
    }));
    // Promotion code block
    cy.get('body').scrollIntoView({
      offset: {
        top: 0,
        left: 0
      }
    });
    cy.get('[data-cy=basket-link]').scrollIntoView().click({
      force: true
    });
    cy.url().should('include', '/checkout/basket');
    cy.validateElement('.c-shopping-cart-promo-code__toggle > button');
    cy.get('.c-shopping-cart-promo-code__toggle > button').click({
      force: true
    });
    cy.validateElement('#default');
    cy.get('#default').click({
      force: true
    }).clear();
    cy.get('#default').click({
      force: true
    }).type(Cypress.env('defaultPromoCode'));
    cy.validateElement('.c-btn.c-btn--accent.c-btn--small');
    cy.get('.c-btn.c-btn--accent.c-btn--small').click({
      force: true
    });
    cy.wait(4000); /* TO KEEP FOR CI TESTS */
    // Make sure the promotion code is applied successfully
    cy.get('body').then(shipping => {
      const promoCodeAppliedSuccessfully = shipping.find('.c-shopping-cart-promo').length > 0;
      switch (true) {
        case promoCodeAppliedSuccessfully:
          cy.validateElement('.c-shopping-cart-promo');
          console.log('Promotion code is applied successfully');
          break;
        default:
          throw new Error('Promotion code is not applied successfully, please check your logs...');
      }
    });
    // API calls capture
    cy.intercept({
      method: 'GET',
      url: '**/v4/secure/checkout/payments/available/**'
    }).as('basketDataValidation');
    cy.intercept({
      method: 'POST',
      url: 'https://stage-data.hipay.com/checkout-data'
    }).as('hipayDataValidation');
    // Continue with the checkout process
    cy.validateElement('[data-cy=validate-basket]').get('[data-cy="validate-basket"]').first().click({
      force: true
    });
    cy.selectShippingMethod();
    // Credit card payment and purchase validation
    cy.frictionlessCreditCardPayment();
    cy.get('.c-confirm-shipping', {
      timeout: 30000
    }).should('be.visible');
    cy.validateElement('.c-confirm__header', {
      timeout: 20000
    }).validateElement('.c-confirm__title').validateElement('.c-confirm__subtitle').validateElement('.c-confirm-shipping');
    cy.visit(store + '/account/history');
    cy.get(':nth-child(1) > .c-order-item', {
      timeout: 20000
    }).should('have.length.gt', 0);
  });
});
