cy.fixture("stores").then(stores => {
  stores.forEach(store => {
    cy.visit(store, {
      failOnStatusCode: false,
      responseTimeout: 10000,
      followRedirect: true
    });
    cy.acceptCookies();
    cy.customLogin();
    cy.listingApiCalls();
    cy.commonApiInterception();
    // Navigate to a valid product listing
    cy.fixture("shippingInfo.json").then(shippingInfo => {
      cy.addShippingAddress();
      cy.visitRandomProductListing(store);
      // Define store && product indexes
      cy.url().then(url => {
        const countryConfig = {
          FRANCE: {
            storeUrl: shippingInfo.FRANCE.storeUrl,
            buttonIndex: 0,
            productIndex: 3
          },
          SPAIN: {
            storeUrl: shippingInfo.SPAIN.storeUrl,
            buttonIndex: 1,
            productIndex: 4
          },
          ITALY: {
            storeUrl: shippingInfo.ITALY.storeUrl,
            buttonIndex: 2,
            productIndex: 5
          }
        };
        // Function to add products to wishlist
        const addProductsToFavorites = (buttonIndex, productIndex) => {
          cy.wait(3000); /* To keep for CI tests */
          cy.get(".c-btn-favoris.c-btn-favoris--small", {
            timeout: 20000
          }).should("be.enabled").should("be.visible").eq(buttonIndex).click({
            force: true
          });
          let validProductIndex = 0;
          cy.wait(3000); /* To keep for CI tests */
          cy.get(".c-product-thumbnail", {
            timeout: 20000
          }).each($el => {
            if (!$el.closest(".c-product-list.nosto_element").length) {
              validProductIndex++;
              if (validProductIndex === productIndex) {
                cy.wrap($el).click({
                  force: true
                });
                return false;
              }
            }
          });
          cy.get(".c-product-sizer__submit > .c-btn-favoris").should("be.visible").click({
            force: true
          });
          cy.wait(2000); /* To keep for CI tests */
        };
        // Determine the country and execute logic
        let countryFound = false;
        for (const [country, config] of Object.entries(countryConfig)) {
          if (url.includes(config.storeUrl)) {
            addProductsToFavorites(config.buttonIndex, config.productIndex);
            countryFound = true;
            break;
          }
        }
        if (!countryFound) {
          throw new Error("Invalid country URL.");
        }
      });
      // Navigate to wishlist page
      cy.get(".c-header-tools__item.c-header-tools__item--favorite").find(".c-header-tools__button").should("have.attr", "href").then(wishlistHref => {
        cy.visit(`${store}${wishlistHref}`, {
          failOnStatusCode: false,
          responseTimeout: 10000,
          followRedirect: true
        });
      });
      cy.validateElement(".c-favorites__list");
      cy.validateElement(".c-favorite-product__remove");
      // Add two products to basket from wishlist
      cy.get("body").then($body => {
        const dropdownSelectorBase = ".c-favorite-product__action > .u-1\\/3 > .c-select > div > #size";
        if ($body.find(dropdownSelectorBase).length > 0) {
          cy.get(".c-favorite-product").each((_, index) => {
            if (index < 2) {
              const specificDropdownSelector = `:nth-child(${index + 1}) > ${dropdownSelectorBase}`;
              // Select the first available option in the dropdown
              cy.get(specificDropdownSelector).then($dropdown => {
                if ($dropdown.length > 0) {
                  cy.wrap($dropdown).find("option").then($options => {
                    if ($options.length > 1) {
                      const firstOption = $options.eq(1).val();
                      cy.wrap($dropdown).select(firstOption);
                      cy.wrap($dropdown).should("have.value", firstOption);
                      const addToBasketButtonSelector = `:nth-child(${index + 1}) .c-btn.c-btn--primary.c-btn--large`;
                      cy.get(addToBasketButtonSelector).eq(1).click({
                        force: true
                      });
                      cy.wait(2000); /* To keep for CI tests */
                      const addToBasketButtonSelector2 = `:nth-child(${index + 2}) .c-btn.c-btn--primary.c-btn--large`;
                      cy.get(addToBasketButtonSelector2).eq(1).click({
                        force: true
                      });
                      cy.wait(2000); /* To keep for CI tests */
                    } else {
                      cy.log(`Not enough options in dropdown ${index + 1}`);
                    }
                  });
                }
              });
            }
          });
        }
      });
      cy.basketValidation();
      cy.selectShippingMethod(store);
      cy.visit(store + "/checkout/payment", {
        failOnStatusCode: false,
        followRedirect: true
      });
      cy.passiveCheckoutCheck(store);
    });
  });
});
