// Use Cypress's retry mechanism to wait until the elements with IDs starting with "crypto-" are no longer in the DOM
function typeAndClickUntilNotPresent() {
  cy.get('body').then($body => {
    cy.wait(5000); /* TO KEEP FOR CI TESTS */
    const cryptoElements = $body.find('[id^="crypto-"]').length > 0;
    if (cryptoElements) {
      cy.get('[id^="crypto-"]').first().should('be.visible').clear({
        force: true
      }).type('123');
      cy.get('[data-cy="submit-basket"]').should('be.enabled').click({
        force: true
      });
      cy.get('[id^="crypto-"]', {
        timeout: 30000
      }).should('not.exist');
      // Recursively call the function if elements are still present
      typeAndClickUntilNotPresent();
    } else {
      cy.log('Success: No more elements with IDs starting with "crypto-"');
    }
  });
}
