// Function to process payment with a saved credit card
function savedCreditCardPaymentProcess() {
  cy.validateElement('[id^="crypto-"]');
  typeAndClickUntilNotPresent();
  cy.validateElement('.c-confirm__header').validateElement('.c-confirm__title').validateElement('.c-confirm__subtitle');
  cy.visit('/account/history', {
    failOnStatusCode: false,
    followRedirect: true,
    responseTimeout: 10000
  });
  cy.get(':nth-child(1) > .c-order-item').should('have.length.gt', 0);
}
// Recursive function to handle dynamic input field
function typeAndClickUntilNotPresent() {
  cy.get('body').then($body => {
    cy.wait(5000); // Required for CI stability
    if ($body.find('[id^="crypto-"]').length > 0) {
      cy.get('[id^="crypto-"]').first().should('be.visible').clear({
        force: true
      }).type('123');
      cy.get('[data-cy="submit-basket"]').should('be.enabled').click({
        force: true
      });
      cy.get('[id^="crypto-"]', {
        timeout: 30000
      }).should('not.exist');
      // Recursively call the function if elements are still present
      typeAndClickUntilNotPresent();
    } else {
      cy.log('Success: No more elements with IDs starting with "crypto-"');
    }
  });
}
typeAndClickUntilNotPresent();
// Proceed with checkout process
cy.addShippingAddress();
cy.addProductToBasket();
cy.productSizer();
cy.basketValidation();
cy.selectShippingMethod();
// API call interception
cy.intercept('GET', '**/v4/secure/checkout/payments/available/**').as('basketDataValidation');
cy.intercept('POST', 'https://stage-data.hipay.com/checkout-data').as('hipayDataValidation');
// Wait for API response
cy.waitForApiResponse('@hipayDataValidation', 202);
// Utility function to handle iframe interaction
const getIframeBody = selector => {
  return cy.get(selector, {
    timeout: 30000
  }).its('0.contentDocument.body').should('not.be.undefined').then(cy.wrap);
};
// Start payment process
cy.wait(3000); /* To keep for CI tests */
cy.get('body').then($body => {
  if ($body.find('.c-payment-method-old-card').length > 0) {
    savedCreditCardPaymentProcess();
  } else {
    // Insert card details
    getIframeBody('#payment-card-custom-number iframe').find('[name=cardnumber]').should('be.visible').type(Cypress.env('customer_cc_number'));
    getIframeBody('#payment-card-custom-date iframe').find('[name=cc-exp]').should('be.visible').type(Cypress.env('customer_cc_expdate'));
    getIframeBody('#payment-card-custom-cvc iframe').find('[name=cvc]').should('be.visible').type(Cypress.env('customer_cc_cvc'));
    // Save card and submit
    cy.validateElement('[id="save-card"]').get('[id^="save-card"]').check({
      force: true
    }).should('be.checked');
    cy.validateElement('[data-cy="submit-basket"]').get('[data-cy="submit-basket"]').click({
      force: true
    });
    // Ensure redirect completes
    cy.url().should('not.include', 'redirect');
    cy.get('#continue-transaction', {
      timeout: 30000
    }).should('be.visible');
    //cy.wait(3000) // Required for CI stability
    cy.url().should('match', /https:\/\/v2-sim\.preprod\.psp-solutions\.com\/acs-simulation\/challenge\?/);
  }
});
// Final transaction confirmation - Navigate and complete the checkout flow again
cy.get('#continue-transaction').should('be.visible').and('not.be.disabled').click({
  force: true
});
cy.wait(5000); /* To keep for CI tests */
cy.visit('/checkout/basket', {
  failOnStatusCode: false,
