cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    cy.visit(store, {
      failOnStatusCode: false,
      responseTimeout: 10000,
      followRedirect: true
    });
    cy.acceptCookies();
    cy.customLogin();
    cy.addShippingAddress();
    cy.listingApiCalls();
    cy.commonApiInterception();
    cy.addProductToBasket();
    cy.productSizer();
    cy.visit(store + '/account', {
      failOnStatusCode: false,
      responseTimeout: 10000,
      followRedirect: true
    });
    // Activate gift cards twice for the process
    for (let i = 0; i < 2; i++) {
      activateGiftCardPinCode();
    }
    // Navigate to product listing and perform further validations
    /*cy.visit(`${store}/sweats-pulls-c8?prices=60~70`, { failOnStatusCode: false, responseTimeout: 10000, followRedirect: true })*/
    cy.wait(3000); /* TO KEEP FOR CI TESTS */
    cy.basketValidation();
    cy.selectShippingMethod();
    // Intercept and validate API calls
    cy.intercept('GET', '**/v4/secure/checkout/payments/available/**').as('basketDataValidation');
    cy.intercept('POST', 'https://stage-data.hipay.com/checkout-data').as('hipayDataValidation');
    // Validate voucher and finalize payment
    validateVoucherSelection();
    cy.creditVoucherPayment();
    validateVoucherPaymentConfirmation();
  });
});
