cy.get("[data-cy=header-tool--account]").first().click();
// Retrieve credentials from login.json
cy.readFile("cypress/fixtures/login.json").then(credentials => {
  cy.validateElement('[data-cy="customer-email"]').get('[data-cy="customer-email"]').type("dummy").wait(1000).clear().should("be.empty").type(credentials.custom_email).should("have.value", credentials.custom_email);
  // Customer password input
  cy.validateElement('[data-cy="customer-password"]').get('[data-cy="customer-password"]').type("dummy").clear().should("be.empty").type(credentials.custom_password).should("have.value", credentials.custom_password);
  // Submit and validate login
  cy.url().should("contain", "login");
  cy.validateElement("[data-cy=submit]").get("[data-cy=submit]").click({
    force: true
  });
  // Use the stored store value for userLastConnectionDateAndStore
  cy.get('@currentStore').then(store => {
    cy.userLastConnectionDateAndStore(store);
  });
  cy.get(".c-form-message--error").should("not.exist");
  // Login API call validation
  cy.intercept("GET", "**/v4/secure/account/addresses").as("securedAddressesApiResponse");
  cy.url().should("contain", "/account/history");
});

