  // Loop through each orderID
  orderIds.forEach(orderId => {
    const updateInvoiceTable = cy.task("queryDb", `INSERT INTO lbov4.facture(commande_id, date) VALUES ('${orderId}', NOW())`).then(() => {
      cy.task("queryDb", `SELECT * FROM facture WHERE commande_id = '${orderId}'`).then(result => {
        expect(result.length).to.be.greaterThan(0);
        expect(result[0].commande_id).to.eq(orderId);
      });
    });
    // Apply necessaire order data modifications
    const updateOrderTable = cy.task("queryDb", `UPDATE lbov4.commande c
          LEFT JOIN lbov4.commande_produit cp ON lbov4.c.commande_id = lbov4.cp.commande_id
          LEFT JOIN lbov4.commande_produit_attribut cpa ON lbov4.cp.commande_produit_id = lbov4.cpa.commande_produit_id
        SET lbov4.c.commande_statut_id = 4,
            c.commande_facture_numero = (SELECT MAX(facture_id) FROM lbov4.facture),
            lbov4.c.commande_facture_date = CURDATE(),
            lbov4.cp.quantity_prepared = lbov4.cp.produit_quantite,
            lbov4.cpa.quantity_prepared = lbov4.cpa.produit_option_valeur_quantite,
            lbov4.c.commande_livraison_colis = '6A16163204582',
            lbov4.c.last_tracking_status = 'DELIVERED',
            lbov4.c.last_tracking_date = CURDATE()
        WHERE lbov4.c.commande_id = '${orderId}'`).then(() => {
      cy.task("queryDb", `SELECT * FROM commande WHERE commande_id = '${orderIds}'`).then(result => {
        expect(result).to.have.lengthOf(1);
        expect(result[0].commande_statut_id).to.equal(4);
        expect(result[0].commande_livraison_colis).to.equal("6A16163204582");
        expect(result[0].last_tracking_status).to.equal("DELIVERED");
      });
    });
  });
});

