cy.validateElement('#mat-input-3');
const getClientOrderId = cy.task("queryDb", `select c2.commande_id from lbov4.commande c2 where c2.client_email = '${this.data.custom_email}' order by c2.commande_date_ajout`).then(Indexcount => {
  // Extract all linked orderIDs this user
  const orderIds = Indexcount.map(record => record['commande_id']);
  // Log the resulting array for further debugging aspects
  cy.log(`Retrieved order IDs: ${orderIds.join(", ")}`);
  // Store unique order IDs in a set
  const uniqueOrderIds = new Set(orderIds);
  // Loop through each unique order ID
  cy.wrap([...uniqueOrderIds]).each(orderId => {
    // Clear and enter the order ID in the input field
    cy.get('[id^="mat-input"]').eq(1).clear().type(orderId);
    // Click the search button
    cy.get(':nth-child(5) > .mat-raised-button > .mat-button-wrapper').click({
      force: true
    });
    // Make sure the selected order is the one we're looking for
    cy.wait(3000); /* TO KEEP FOR CI TESTS */
    cy.validateElement('.mat-row > .cdk-column-order');
    cy.get('.mat-row > .cdk-column-order', {
      timeout: 0
    }).invoke('text').then(text => {
      const orderIdsArray = text.split(',').map(Number).filter(Number.isInteger);
      const containsOrderId = orderIdsArray.includes(orderId);
      expect(containsOrderId).to.be.true;
      // Make sure the order ID is not used again in the loop
      const orderIdCount = orderIdsArray.filter(id => id === orderId).length;
      expect(orderIdCount).to.equal(1);
    });
    // Click the row to open the order details
    cy.validateElement('.mat-row');
    cy.get('.mat-row').click({
      force: true
    });
    // Wait for the order details to load
    cy.validateElement('.mat-headline').wait(5000); /* TO KEEP FOR CI TESTS */
    // Check if the size block exists
    cy.get('body').then($ele => {
      if ($ele.find(':nth-child(2) > .mat-raised-button > .mat-button-wrapper').length > 0) {
        // If the size block exists, validate the elements on the page
        cy.validateElement('[class^="title"]');
        cy.validateElement('[class^="content-container"]');
        cy.validateElement('.sidenav-scrollbar-container');
        cy.validateElement('.content-container > :nth-child(5) > :nth-child(1)');
        cy.validateElement('.content-container > :nth-child(5) > :nth-child(2)');
        cy.validateElement('[class^="mat-checkbox-inner-container"]');
        cy.validateElement(':nth-child(1) > .mat-raised-button > .mat-button-wrapper');
        cy.validateElement(':nth-child(2) > .mat-raised-button > .mat-button-wrapper');
        // Retrieve the orderReturnId from the DBMS
        const getOrderReturnId = cy.task("queryDb", `select ro.id from lbov4.return_order ro where ro.commande_id = '${orderId}' order by ro.created DESC limit 1`).then(Indexcount => {
          // Isolate the OrderID (index based)
          var OrderReturnId = Indexcount[0]['id'];
          cy.log(OrderReturnId);
          // Return credit create PATCH call
          cy.intercept({
            method: 'POST',
            url: '**/return_order/' + OrderReturnId + '/credit/create'
          }).as('returnOrderCreditCreatePOSTCall');
          // Return validation PATCH call
          cy.intercept({
            method: 'PATCH',
            url: '**/return_order/' + OrderReturnId + '/patch'
          }).as('returnOrderPATCHCall');
          // Return validation POST notify call
          cy.intercept({
            method: 'POST',
            url: '**/return_order/' + OrderReturnId + '/notify/refund'
          }).as('returnOrderNotifyPOSTCall');
          // Return order payment validation POST call
          cy.intercept({
            method: 'POST',
            url: '**/return_order_payment/create'
          }).as('returnOrderPOSTCall');
          // Return order status GET call
          cy.intercept({
            method: 'GET',
            url: '**/return_order/status'
          }).as('returnOrderStatusGETCall');
        });
        // Submit the request
        cy.get('[class^=mat-checkbox-inner-container');
        cy.get('[type="checkbox"]').eq(2).check({
          force: true
        });
        cy.get(':nth-child(2) > .mat-raised-button > .mat-button-wrapper').click({
          force: true
        });
        // Validate API caching et response status code ==> wait for response.status to be either 200, 204 or 201  
        cy.wait('@returnOrderPATCHCall').its('response.statusCode').should('equal', 204);
        cy.wait('@returnOrderPOSTCall').its('response.statusCode').should('equal', 201);
        cy.wait('@returnOrderCreditCreatePOSTCall').its('response.statusCode').should('equal', 201);
      } else {
        cy.log('This orderID has already been treated');
        cy.go('back');
      }
    });
  });
