cy.fixture('shippingInfo.json').then(({
  FRANCE
}) => {
  cy.fixture('stores').then(stores => {
    // Only select stores that match the FRANCE store URL
    const franceStores = stores.filter(store => store.includes(FRANCE.storeUrl));
    franceStores.forEach(store => {
      cy.visit(store, {
        failOnStatusCode: false,
        responseTimeout: 10000,
        followRedirect: true
      });
      cy.acceptCookies();
      cy.customLogin();
      cy.addShippingAddress();
      cy.listingApiCalls();
      cy.commonApiInterception();
      cy.addProductToBasket();
      cy.productSizer();
      cy.visit(store + '/account', {
        failOnStatusCode: false,
        responseTimeout: 10000,
        followRedirect: true
      });
      // Activate gift card (only once here)
      for (let i = 0; i < 2; i++) {
        activateGiftCardPinCode();
      }
      // Proceed with checkout after gift card is applied
      // cy.wait(3000) // Optional wait for CI stability if needed
      cy.basketValidation();
      cy.selectShippingMethod();
      cy.intercept('GET', '**/v4/secure/checkout/payments/available/**').as('basketDataValidation');
      cy.intercept('POST', 'https://stage-data.hipay.com/checkout-data').as('hipayDataValidation');
      validateVoucherSelection();
      cy.creditVoucherPayment();
      validateVoucherPaymentConfirmation();
    });
  });
});
