cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    cy.visit(store);
    cy.acceptCookies();
    cy.visit(`${store}/login?mode=connection`, {
      failOnStatusCode: false
    });
    cy.validateElement('[data-cy=customer-registration]').wait(2000);
    cy.get('[data-cy=customer-registration]').click({
      force: true
    });
    cy.url().should('contain', 'login?mode=connection');
    // Customer registration
    cy.acceptCookies();
    cy.customInfo();
    // Confirm subscription
    cy.validateElement('[data-cy=submit] > .c-btn');
    cy.get('[data-cy=submit] > .c-btn').click({
      force: true
    });
    cy.wait(3000); /* TO KEEP FOR CI TESTS */
    cy.validateElement('.c-form-message');
  });
});

