// Logo
validateElementAndChildren('[class="nuxt-link-exact-active nuxt-link-active"]');
cy.get('.nuxt-link-exact-active.nuxt-link-active').invoke('attr', 'href').then(href => {
  expect(href).to.equal('/');
  cy.request(href).its('status').should('eq', 200);
});
// Search bar
validateElementAndChildren('[class="autosuggest c-btn-with-input"]');
// Help link based on store
cy.validateElement('[class="c-header-tools__link"]');
cy.url().then(currentUrl => {
  cy.fixture('shippingInfo.json').then(shippingInfo => {
    const hrefAssertions = {
      [shippingInfo.FRANCE.storeUrl]: 't/faq',
      [shippingInfo.SPAIN.storeUrl]: 't/ayuda',
      [shippingInfo.ITALY.storeUrl]: 't/assistenza'
    };
    const matchedStore = Object.keys(hrefAssertions).find(store => currentUrl.includes(store));
    const expectedHref = matchedStore ? hrefAssertions[matchedStore] : null;
    if (expectedHref) {
      cy.get('[class="c-header-tools__link"]').invoke('attr', 'href').then(href => {
        expect(href).to.equal(expectedHref);
        cy.request(currentUrl + href).its('status').should('eq', 200);
      });
    } else {
      throw new Error('Could not determine store context for help link.');
    }
  });
});
// Header tools (account, wishlist, etc.)
validateElementAndChildren('[data-cy=header-tool--account]');
validateElementAndChildren('.c-header-tools__item--favorite');
cy.get('.c-header-tools__list').find('a').each($link => {
  const href = $link.attr('href');
  expect(href).to.exist.and.to.not.be.empty;
  retryAnchorGetRequest(url, href);
});
// Store switcher
cy.get('[class="c-international-switch__list"]').then(() => {
  validateElementAndChildren('[data-cy="lang-link"]');
  cy.get('[data-cy="lang-link"]').click();
  cy.get('.c-header-tools__item--lang').click();
});
cy.get('.c-dropdown-menu__panel', {
  force: true
}).find('a[href$=".fr"], a[href$=".es"], a[href$=".it"]').each($link => {
  const href = $link.attr('href');
  cy.request(href).then(response => {
    expect(response.status).to.eq(200);
    expect(response.headers['content-type']).to.include('text/html');
  });
});

validateElementAndChildren('.c-reinsurance');
cy.acceptCookies();
// Newsletter
validateElementAndChildren('.c-newsletter-form');
validateElementAndChildren('.c-btn-with-input.c-btn-with-input--reverse');
cy.get('.c-btn-with-input.c-btn-with-input--reverse').as('newsletter');
cy.get('@newsletter').find('> input').click({
  force: true
}).should('have.focus');
cy.get('@newsletter').type('newsletter@lbo.com', {
  delay: 200,
  force: true
});
cy.get('.c-btn.c-btn--reverse').should('not.have.class', 'c-btn--disabled');
// Social networks
validateElementAndChildren('.c-footer-social-network');
validateElementAndChildren('.c-footer-app');
cy.url().then(currentUrl => {
  cy.fixture('shippingInfo.json').then(shippingInfo => {
    const oneLinkFrance = Cypress.env('oneLinkFr');
    const oneLinkRyses = Cypress.env('oneLinkRyses');
    cy.get('.c-footer-app__list').should('have.length', 1).within(() => {
      let expectedLinks = [];
      if (currentUrl.includes(shippingInfo.FRANCE.storeUrl)) {
        expectedLinks = [oneLinkFrance];
      } else if (currentUrl.includes(shippingInfo.SPAIN.storeUrl) || currentUrl.includes(shippingInfo.ITALY.storeUrl)) {
        expectedLinks = [oneLinkRyses];
      } else {
        throw new Error('Unknown store URL for social/app links.');
      }
      expectedLinks.forEach(link => {
        cy.get(`a[href*="${link}"]`).should('have.length', 2).each($el => {
          cy.wrap($el).should('be.visible');
          cy.wrap($el).invoke('attr', 'href').then(href => {
            if (href && href.startsWith('http')) {
              cy.request(href).its('status').should('eq', 200);
            } else {
              cy.log(`Skipping non-http link: ${href}`);
            }
          });
        });
      });
    });
  });
});
// Footer sitemap
validateElementAndChildren('.c-footer-sitemap');
cy.get('.c-footer-sitemap a').should('have.length.gt', 0).each(($el, index, $list) => {
  const href = $el.attr('href');
  if (href) {
    retryAnchorGetRequest(url, href);
    if (index < $list.length - 1) cy.wait(2000);
  }
});

