    }, {
      responseTimeout: 10000
    }, {
      followRedirect: true
    });
    // Call address block
    cy.validateElement('a[href="/account/address-book"]');
    cy.get('a[href="/account/address-book"]').click({
      force: true
    });
    cy.url().should('contain', '/account/address-book');
    // Add a new addresse
    cy.validateElement('.c-account-address-book > .c-btn');
    cy.get('.c-account-address-book > .c-btn').click({
      force: true
    });
    cy.validateElement('[class="c-account-form__item c-account-form__right"]');
    // Fill-up shipping address based on store and validate
    cy.get('.c-account-form__item.c-account-form__right').then($ele => {
      // Check for input field presence
      if ($ele.find('.c-account-form__input').length > 0) {
        cy.url().then(url => {
          // Load shipping info based on the selected store
          cy.fixture('shippingInfo.json').then(shippingInfo => {
            let randomPhoneNumber, countryData;
            if (url.includes(shippingInfo.FRANCE.storeUrl)) {
              countryData = shippingInfo.FRANCE;
              randomPhoneNumber = generateRandomPhoneNumber("+336");
            } else if (url.includes(shippingInfo.SPAIN.storeUrl)) {
              countryData = shippingInfo.SPAIN;
              randomPhoneNumber = generateRandomPhoneNumber("+346");
            } else if (url.includes(shippingInfo.ITALY.storeUrl)) {
              countryData = shippingInfo.ITALY;
              randomPhoneNumber = generateRandomPhoneNumber("+396");
            } else {
              throw new Error('Unable to determine store URL, check logs...');
            }
            fillAddressDetails("CypressFirstNameChanged", "CypressLastNameChanged", randomPhoneNumber, `${countryData.address}`, `${countryData.city}`, `${countryData.postalCode}`);
            cy.validateElement('.c-account-form__submit > .c-btn');
            cy.get('.c-account-form__submit > .c-btn').click({
              force: true
            });
            cy.url().should("contain", "/account/address-book?confirm=1");
            cy.validateElement('.c-form-message.c-form-message--success.c-form-message--fixed');
          });
        });
      }
    });
    // Helper function to generate a random phone number
    function generateRandomPhoneNumber(prefix) {
      return `${prefix}${Math.floor(Math.random() * 1000000000).toString().padStart(9, '0')}`;
    }
    // Helper function to throw an error if needed
    function throwError(message) {
      throw new Error(message);
    }
  });
});

cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    cy.visit(store, {
      failOnStatusCode: false,
      responseTimeout: 10000,
      followRedirect: true
    });
    cy.acceptCookies();
    cy.customLogin();
    cy.listingApiCalls();
    cy.commonApiInterception();
    cy.visitRandomProductListing(store, {
      priceRange: 'prices=5~10',
      category: '/accessoires-c1?'
    });
    function selectAvailableSizeFromListing() {
      cy.get('body').should('be.visible');
      cy.get('.c-product-thumbnail', {
        timeout: 20000
      }).should('be.visible').then($products => {
        // Exclude Nosto products
        const validProducts = $products.not('.c-product-list.nosto_element .c-product-thumbnail');
        if (validProducts.length > 0) {
          cy.log(`Found ${validProducts.length} valid products, selecting one...`);
          selectProduct(validProducts).then(() => {
            cy.log(`Successfully selected a product.`);
            // Now on product page, proceed with size selection
            return cy.then(() => {
              return selectAvailableSizeOrRetry();
            });
          });
        } else {
          throw new Error('No valid products found in main listing.');
        }
      });
    }
    function selectProduct(validProducts) {
      return cy.wrap(validProducts).then($products => {
        const randomIndex = Math.floor(Math.random() * Math.min(48, $products.length)); // max 48 to avoid Nosto
        cy.wrap($products.eq(randomIndex)).find('.c-product-thumbnail__img-wrapper').should('be.visible').click({
          force: true
        });
      });
    }
    function selectAvailableSizeOrRetry() {
      return cy.get('body').then($body => {
        cy.wait(2000); /* TO KEEP FOR CI TESTS */
        // OOS email input check
        const oosInput = $body.find('.c-product-sizer-oos__email-input');
        if (oosInput.length > 0 && Cypress.$(oosInput).is(':visible')) {
          cy.log('OOS email input visible. Going back and selecting another product...');
          cy.go('back');
          return selectAvailableSizeFromListing();
        }
        // Modal check
        if ($body.find('.c-modal-simple:visible').length > 0) {
          cy.log('Modal appeared. Going back and selecting another product...');
          cy.go('back');
          return selectAvailableSizeFromListing();
        }
        // Out of stock message check
        if ($body.find('.c-product-sizer-oos:visible').length > 0) {
          cy.log('Product out of stock. Going back and selecting another product...');
          cy.go('back');
          return selectAvailableSizeFromListing();
        }
        // Side panel check
        return cy.get('.c-product__side', {
          timeout: 20000
        }).then($sidePanel => {
          if ($sidePanel.length === 0) {
            cy.log('Product page did not load correctly. Going back and selecting another product...');
            cy.go('back');
            return selectAvailableSizeFromListing();
          }
          const hasSizeOptions = $sidePanel.find('.c-product-sizer__list').length > 0;
          if (hasSizeOptions) {
            return selectAndAddToBasket();
          } else {
            return addToBasket();
          }
        });
      });
    }
    function selectAndAddToBasket() {
      cy.log('Selecting available size...');
      cy.wait(2000); /* TO KEEP FOR CI TESTS */
      return cy.get('[data-cy=size-chart] > [data-cy=product-size-chart]', {
        timeout: 20000
      }).should('exist').then(() => {
        return cy.get('.c-option:not(.c-option--oos)', {
          timeout: 20000
        }).then($sizes => {
          if ($sizes.length > 0) {
            cy.wrap($sizes.first()).click();
            return addToBasket();
          } else {
            cy.log('All sizes out of stock. Going back and selecting another product...');
            cy.go('back');
            return selectAvailableSizeFromListing();
          }
        });
      });
    }
    function addToBasket() {
      cy.log('Adding product to basket...');
      cy.wait(2000); /* TO KEEP FOR CI TESTS */
      return cy.get('[data-cy="add-to-basket"]', {
        timeout: 20000
      }).eq(0).should('exist').should('be.visible').click({
        force: true
      }).then(() => {
        cy.get('[data-cy=basket-count]', {
          timeout: 20000
        }).should('not.be.empty');
        cy.log('Product successfully added to the basket.');
      });
    }
    selectAvailableSizeFromListing(store);
    cy.basketValidation(store);
    cy.selectShippingMethod(store);
    cy.visit(store + "/checkout/payment", {
      failOnStatusCode: false,
      followRedirect: true
    });
  });
});
