cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    cy.visit(store, {
      failOnStatusCode: false,
      responseTimeout: 10000,
      followRedirect: true
    });
    cy.acceptCookies();
    cy.customLogin(store);
    cy.listingApiCalls();
    cy.commonApiInterception();
    cy.addShippingAddress();
    /**/
    cy.visitRandomProductListing(store, {
      priceRange: 'prices=5~10',
      category: '/accessoires-c1?'
    });
    function selectAvailableSizeFromListing(index = 0) {
      cy.get('body').should('be.visible');
      cy.get('.c-product-thumbnail', {
        timeout: 20000
      }).should('be.visible').then($products => {
        // Filter out Nosto products
        const validProducts = $products.not('.c-product-list.nosto_element .c-product-thumbnail');
        if (validProducts.length === 0) {
          throw new Error('No valid products found in main listing.');
        }
        if (index >= 4 || index >= validProducts.length) {
          throw new Error('No available product with sizes after trying 4 options.');
        }
        cy.log(`Trying product at index ${index}`);
        selectProduct(validProducts, index).then(() => {
          return selectAvailableSizeOrRetry(index);
        });
      });
    }
    function selectProduct(validProducts, index) {
      return cy.wrap(validProducts).then($products => {
        cy.wrap($products.eq(index)).find('.c-product-thumbnail__img-wrapper').should('be.visible').click({
          force: true
        });
      });
    }
    function selectAvailableSizeOrRetry(index) {
      cy.wait(2000); /* TO KEEP FOR CI TESTS */
      return cy.get('body', {
        timeout: 20000
      }).then($body => {
        if ($body.find('.c-product-sizer-oos').length > 0 && $body.find('.c-product-sizer-oos').is(':visible')) {
          cy.log('Out-of-stock size detected. Trying next product...');
          cy.go('back');
          return selectAvailableSizeFromListing(index + 1);
        }
        // Check modals
        if ($body.find('.c-modal-simple:visible').length > 0) {
          cy.log('Modal detected. Trying next product...');
          cy.go('back');
          return selectAvailableSizeFromListing(index + 1);
        }
        // Check OOS message
        if ($body.find('.c-product-sizer-oos:visible').length > 0) {
          cy.log('Out-of-stock message detected. Trying next product...');
          cy.go('back');
          return selectAvailableSizeFromListing(index + 1);
        }
        // Check if product page loaded correctly
        return cy.get('.c-product__side', {
          timeout: 20000
        }).then($sidePanel => {
          if ($sidePanel.length === 0) {
            cy.log('Product page did not load. Trying next product...');
            cy.go('back');
            return selectAvailableSizeFromListing(index + 1);
          }
          const hasSizeOptions = $sidePanel.find('.c-product-sizer__list').length > 0;
          if (hasSizeOptions) {
            return selectAndAddToBasket(index);
          } else {
            return addToBasket();
          }
        });
      });
    }
    function selectAndAddToBasket(index) {
      cy.log('Selecting available size...');
      cy.wait(2000); /* TO KEEP FOR CI TESTS */
      return cy.get('[data-cy=size-chart] > [data-cy=product-size-chart]', {
        timeout: 20000
      }).should('exist').then(() => {
        return cy.get('.c-option:not(.c-option--oos)', {
          timeout: 20000
        }).then($sizes => {
          if ($sizes.length > 0) {
            cy.wrap($sizes.first()).click();
            return addToBasket();
          } else {
            cy.log('All sizes out of stock. Trying next product...');
            cy.go('back');
            return selectAvailableSizeFromListing(index + 1);
          }
        });
      });
    }
    function addToBasket() {
      cy.log('Adding product to basket...');
      cy.wait(2000); /* TO KEEP FOR CI TESTS */
      return cy.get('[data-cy="add-to-basket"]', {
        timeout: 20000
      }).eq(0).should('exist').should('be.visible').click({
        force: true
      }).then(() => {
        cy.get('[data-cy=basket-count]', {
          timeout: 20000
        }).should('not.be.empty');
        cy.log('Product successfully added to the basket.');
      });
    }
    selectAvailableSizeFromListing();
    /**/
    cy.basketValidation();
    cy.selectShippingMethod();
    cy.intercept('GET', '**/v4/secure/checkout/payments/available/**').as('basketDataValidation');
    cy.intercept('POST', 'https://stage-data.hipay.com/checkout-data').as('hipayDataValidation');
    validateVoucherSelection();
    cy.creditVoucherPayment();
    validateVoucherPaymentConfirmation();
  });
});

