cy.fixture('stores').then(stores => {
  stores.forEach(store => {
    // Visit the store and login
    cy.visit(store, {
      failOnStatusCode: false,
      responseTimeout: Cypress.env('tailoredResponseTimeout')
    });
    cy.acceptCookies();
    cy.customLogin();
    cy.addShippingAddress();
    // Add free-size product to basket from a random category
    const allCategories = ['/bolsos-c192?', '/bonnets-c51?', '/bracelets-c52?', '/sacs-a-dos-c190?', '/lunettes-de-soleil-c71?'];
    // Randomly select a category using Cypress's built-in Lodash method
    cy.visitRandomProductListing(store, {
      priceRange: 'prices=20~40',
      category: '/accessoires-c1?'
    });
    cy.AddProductToBasketForFilteredListing();
    // Repeat basket creation process multiple times
    Cypress._.times(Cypress.env('numberOfRuns'), () => cy.basketCreationProcess({
      store
    }));
    // Apply promo code and check
    cy.visit(`${store}/checkout/basket`, {
      failOnStatusCode: false,
      followRedirect: true
    });
    cy.validateElement('.c-shopping-cart-promo-code__toggle > button');
    cy.get('.c-shopping-cart-promo-code__toggle > button').click({
      force: true
    });
    cy.get('[id="default"]').clear().type(Cypress.env('defaultPromoCode'));
    cy.get('.c-btn.c-btn--accent.c-btn--small').click({
      force: true
    });
    cy.get('.c-shopping-cart-promo').invoke('text').then(text => {
      const normalizedText = text.replace(/\s+/g, ' ').trim();
      expect(normalizedText).to.contain(Cypress.env('defaultPromoCode'));
    });
    // Complete the checkout process
    cy.selectShippingMethod(store);
    // Capture API calls
    cy.intercept('GET', '**/v4/secure/checkout/payments/available/**').as('basketDataValidation');
    cy.intercept('POST', 'https://stage-data.hipay.com/checkout-data').as('hipayDataValidation');
    // Payment and finalization
    cy.fixture('shippingInfo.json').then(shippingInfo => {
      const paymentMethod = store.includes(shippingInfo.FRANCE.storeUrl) ? 'ChÃ¨que' : store.includes(shippingInfo.SPAIN.storeUrl) || store.includes(shippingInfo.ITALY.storeUrl) ? 'Cheque' : null;
      if (!paymentMethod) {
        throw new Error('Invalid country store.');
      }
      cy.visit(`${store}/checkout/payment`, {
        failOnStatusCode: false,
        followRedirect: true
      });
      cy.validateElement('.c-payment-method-item__wrapper > :nth-child(2) > :nth-child(1) > .c-payment-method-item__button');
      cy.get('.c-payment-method-item__wrapper > :nth-child(2) > :nth-child(1) > .c-payment-method-item__button').click({
        force: true
      });
      cy.get(`[alt="${paymentMethod}"]`).click({
        force: true
      });
      cy.get('[data-cy="submit-basket"]').click({
        force: true
      });
      cy.validateElement('.c-confirm__header');
      cy.validateElement('.c-confirm__title');
      cy.validateElement('.c-confirm__subtitle');
    });
    // Verify the new order in order history
    cy.visit(`${store}/account/history`, {
      failOnStatusCode: false,
      followRedirect: true
    });
    cy.get(':nth-child(1) > .c-order-item', {
      timeout: 20000
    }).should('have.length.gt', 0);
  });
});
